import React, { use, useEffect } from "react";
import "./App.css";

type Product = {
  id: string;
  name: string;
  description: string;
  price: number;
};

type Label = {
  name: string;
};

const AddProduct: React.FC = () => {
  const [products, setProducts] = React.useState<Product[]>([]);
  const [labels, setLabels] = React.useState<Label[]>([]);
  const [selectedLabels, setSelectedLabels] = React.useState<Label[]>([]);

  function handleLabelClick(label: Label) {
    // first check if the label is already selected
    const isSelected = selectedLabels.some((selectedLabel) => {
      return selectedLabel.name === label.name;
    });
    if (isSelected) {
      // if it is selected, remove it from the selected labels
      setSelectedLabels((prev) =>
        prev.filter((selectedLabel) => selectedLabel.name !== label.name)
      );
    } else {
      // if it is not selected, add it to the selected labels
      setSelectedLabels((prev) => [...prev, label]);
    }
  }

  async function fetchProducts() {

    let url = "https://7kat3yi6ui.execute-api.eu-west-1.amazonaws.com/products";

    if (selectedLabels.length > 0) {
      const selectedLabelNames = selectedLabels.map((label) => label.name);
      url += `?labels=${selectedLabelNames.join(",")}`;
    }

    await fetch(
      url,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          mode: "cors",
        },
      }
    )
      .then(async (response) => {
        if (response.ok) {
          const data = await response.json();
          setProducts(data);
          console.log("Products fetched successfully:", data);
        } else {
          alert("Error fetching products");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
        alert("Error fetching products");
      });
  }

  async function fetchLabels() {
    await fetch(
      "https://7kat3yi6ui.execute-api.eu-west-1.amazonaws.com/labels",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          mode: "cors",
        },
      }
    )
      .then(async (response) => {
        if (response.ok) {
          const data = await response.json();
          setLabels(data);
          console.log("Labels fetched successfully:", data);
        } else {
          alert("Error fetching labels");
        }
      })
      .catch((error) => {
        console.error("Error:", error);
        alert("Error fetching labels");
      });
  }

  useEffect(() => {
    fetchProducts();
    fetchLabels();
  }, []);

    useEffect(() => {
      fetchProducts();
    }, [selectedLabels]);

  return (
    <div>
      <h1>View Products</h1>

      <div className="labels-container">
        {labels.map((label) => {
          const isSelected = selectedLabels.some(
            (selectedLabel) => selectedLabel.name === label.name
          );
          return (
            <div
              className={`label${isSelected ? " selected" : ""}`}
              onClick={() => handleLabelClick(label)}
            >
              {label.name}
            </div>
          );
        })}
      </div>

      <div className="products-container">
        {products.map(async (product) => {
            // check if the product has an image property

            const imageUrl = `https://jakob-team-product-images.s3.eu-west-1.amazonaws.com/${product.id}`;
            const [imageExists, setImageExists] = React.useState<boolean>(true);

            React.useEffect(() => {
                let isMounted = true;
                fetch(imageUrl, { method: "HEAD" })
                    .then((res) => {
                        if (isMounted) setImageExists(res.ok);
                    })
                    .catch(() => {
                        if (isMounted) setImageExists(false);
                    });
                return () => { isMounted = false; };
            }, [imageUrl]);

            return (
                <div className="product">
                    {imageExists && (
                        <img
                            src={imageUrl}
                            alt={product.name}
                            className="product-image"
                        />
                    )}
                    <h3>{product.name}</h3>
                    <p className="product-description">{product.description}</p>
                    <p className="product-price">{product.price}</p>
                </div>
            );
        })}
      </div>
    </div>
  );
};

export default AddProduct;
