import React from "react";
import "./App.css";
import { Product } from "./ViewProducts";

export const ProductCard: React.FC<{ product: Product }> = ({ product }) => {
  const imageUrl = `https://jakob-team-product-images.s3.eu-west-1.amazonaws.com/${product.id}`;
  const [imageExists, setImageExists] = React.useState(true);

  React.useEffect(() => {
    console.log("Checking if image exists at URL:", imageUrl);
    let isMounted = true;
    fetch(imageUrl, { method: "HEAD" })
      .then((res) => {
        if (isMounted) setImageExists(res.ok);
      })
      .catch(() => {
        if (isMounted) setImageExists(false);
      });
    return () => { isMounted = false; };
  }, [imageUrl]);

  return (
    <div className="product">
      {imageExists && (
        <img
          src={imageUrl}
          alt={product.name}
          className="product-image"
        />
      )}
      <h3>{product.name}</h3>
      <p className="product-description">{product.description}</p>
      <p className="product-price">{product.price}</p>
    </div>
  );
};
